import React, { useState, useEffect } from 'react';
import TodoList from './components/TodoList';
import AddTask from './components/AddTask';
import axios from 'axios';

function App() {
    const [tasks, setTasks] = useState([]);

    // Fetch tasks from the backend when the component mounts
    useEffect(() => {
        axios.get('http://localhost:5003/tasks')
            .then(response => setTasks(response.data))
            .catch(error => console.error('Error fetching tasks:', error));
    }, []);

    // This function will be called when a new task is added
    const handleTaskAdded = (newTask) => {
        setTasks([...tasks, newTask]);  // Update the state with the new task
    };

    return (
        <div>
            <h1>To-Do List App</h1>
            <AddTask onTaskAdded={handleTaskAdded} />  // Pass the function to AddTask component
            <TodoList tasks={tasks} setTasks={setTasks} />  // Pass tasks state to TodoList
        </div>
    );
}

export default App;
